mapper.select:
SELECT a.*,b.category_ deploymentCategory FROM( SELECT t1.ID_ AS ID, t1.CATEGORY_ AS CATEGORY, t1.NAME_ AS NAME, t1.KEY_ AS KEY, t1.VERSION_ AS VERSION, t1.DESCRIPTION_ AS DESCRIPTION, t1.SUSPENSION_STATE_ AS STATE, t1.DEPLOYMENT_ID_ AS deploymentId, ROW_NUMBER() OVER(PARTITION BY t1.KEY_ ORDER BY t1.version_ desc) AS RN FROM act_re_procdef t1 ) a LEFT JOIN act_re_deployment b ON a.deploymentId = b.id_ WHERE a.RN = 1
and a.name like '%'||#{name}||'%'
and a.category = #{category}
and a.state = #{state}
ORDER BY a.name
