(function(a){a.store=a.store||{};a.store.set=function(b,d,c){if(c){store.set(b,{val:d,exp:c,time:new Date().getTime()})}else{store.set(b,d)}};a.store.get=function(b){var c=store.get(b);if(!c){return null}if(a.isPlainObject(c)&&c.exp){if(new Date().getTime()-c.time>c.exp){store.remove(b);return null}return c.val}else{return c}};a.store.has=function(b){store.has(b)};a.store.remove=function(b){store.remove(b)};a.store.clear=function(){store.clear()};a.store.transact=function(b,d,c){store.transact(b,d,c)};a.store.getAll=function(){return store.getAll()};a.store.forEach=function(b){store.forEach(b)};a.store.serialize=function(b){store.serialize(b)};a.store.deserialize=function(b){store.deserialize(b)}}(jQuery));
